import { ToastAndroid, Platform } from 'react-native';

declare const window: any;
declare const Notification: any;
declare const alert: (message: string) => void;

export const showToast = (message: string) => {
  if (Platform.OS === 'android') {
    ToastAndroid.showWithGravity(
      message,
      ToastAndroid.SHORT,
      ToastAndroid.CENTER,
    );
  } else if (Platform.OS === 'web') {
    // Use browser's native toast for web
    if (typeof window !== 'undefined' && 
        'Notification' in window && 
        window.Notification.permission === 'granted') {
      new window.Notification(message);
    } else {
      window.alert(message); // Fallback to alert for web
    }
  } else {
    // For iOS or other platforms, we could add a custom toast component here
    console.log(message);
  }
};
